function myFunction(){var e=document.getElementById("fromSelect").value,t=document.getElementById("toSelect").value,n=document.getElementById("toConvert").value/e*t;document.getElementById("convertedValue").innerHTML=n.toFixed(2)}$.ajax({url:"http://api.fixer.io/latest",method:"GET"}).done(function(e){var t=Object.keys(e.rates).map(function(t){return{country:t,rate:e.rates[t]}}),n=document.getElementById("from"),o=document.createElement("select");o.id="fromSelect",n.appendChild(o);for(a=0;a<t.length;a++)(d=document.createElement("option")).value=t[a].rate,d.text=t[a].country,o.appendChild(d);var c=document.getElementById("to"),l=document.createElement("select");l.id="toSelect",c.appendChild(l);for(var a=0;a<t.length;a++){var d=document.createElement("option");d.value=t[a].rate,d.text=t[a].country,l.appendChild(d)}var r=document.createElement("euro");r.value=1,r.text="EUR",l.appendChild(r)}).fail(function(e){throw e}),document.getElementById("calculateButton").onclick=myFunction();